import{_ as e,c as a,o as r,a as n}from"./app.0748e82a.js";const k=JSON.parse('{"title":"\u5783\u573E\u56DE\u6536\u673A\u5236","description":"","frontmatter":{"title":"\u5783\u573E\u56DE\u6536\u673A\u5236","outline":[2,6]},"headers":[{"level":2,"title":"\u5185\u5B58\u5206\u4EE3","slug":"\u5185\u5B58\u5206\u4EE3","link":"#\u5185\u5B58\u5206\u4EE3","children":[]},{"level":2,"title":"\u65B0\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536(Scavenge)","slug":"\u65B0\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536-scavenge","link":"#\u65B0\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536-scavenge","children":[]},{"level":2,"title":"\u8001\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536 (Mark-Sweep & Mark-Compact)","slug":"\u8001\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536-mark-sweep-mark-compact","link":"#\u8001\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536-mark-sweep-mark-compact","children":[]},{"level":2,"title":"Incremental Marking (\u589E\u91CF\u6807\u8BB0)","slug":"incremental-marking-\u589E\u91CF\u6807\u8BB0","link":"#incremental-marking-\u589E\u91CF\u6807\u8BB0","children":[]},{"level":2,"title":"\u5185\u5B58\u6CC4\u6F0F","slug":"\u5185\u5B58\u6CC4\u6F0F","link":"#\u5185\u5B58\u6CC4\u6F0F","children":[]}],"relativePath":"summary/Other/gc.md","lastUpdated":1677489973000}'),i={name:"summary/Other/gc.md"},t=n('<p>V8\u5F15\u64CE\u7684\u5783\u573E\u56DE\u6536\u673A\u5236\u662F\u91C7\u7528\u4E86\u4E00\u79CD<strong>\u5206\u4EE3\u5F0F\u56DE\u6536\u673A\u5236</strong>\uFF0C\u6839\u5177\u5BF9\u8C61\u5B58\u6D3B\u65F6\u95F4\u8FDB\u884C\u5206\u4EE3\uFF0C\u5C06\u5185\u5B58\u5206\u4E3A\u65B0\u751F\u4EE3\u548C\u8001\u751F\u4EE3\u4E24\u5757\u5185\u5B58\u7A7A\u95F4\u3002</p><h2 id="\u5185\u5B58\u5206\u4EE3" tabindex="-1">\u5185\u5B58\u5206\u4EE3 <a class="header-anchor" href="#\u5185\u5B58\u5206\u4EE3" aria-hidden="true">#</a></h2><ul><li>\u65B0\u751F\u4EE3\uFF1A\u5176\u4E2D\u7684\u5BF9\u8C61\u5B58\u6D3B\u65F6\u95F4\u8F83\u77ED</li><li>\u8001\u751F\u4EE3\uFF1A\u5176\u4E2D\u7684\u5B58\u6D3B\u65F6\u95F4\u8F83\u957F\u6216\u5E38\u9A7B\u5185\u5B58\u7684\u5BF9\u8C61</li></ul><h2 id="\u65B0\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536-scavenge" tabindex="-1">\u65B0\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536(Scavenge) <a class="header-anchor" href="#\u65B0\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536-scavenge" aria-hidden="true">#</a></h2><p>\u65B0\u751F\u4EE3\u7684\u5BF9\u8C61\u4E3B\u8981\u901A\u8FC7 Scavenge \u7B97\u6CD5\u8FDB\u884C\u5783\u573E\u56DE\u6536\u3002Scavenge \u7B97\u6CD5\u7684\u5177\u4F53\u5B9E\u73B0\u4E2D\u91C7\u7528\u4E86 Cheney \u7B97\u6CD5\u3002</p><h4 id="\u5185\u5B58\u4E00\u5206\u4E3A\u4E8C" tabindex="-1">\u5185\u5B58\u4E00\u5206\u4E3A\u4E8C <a class="header-anchor" href="#\u5185\u5B58\u4E00\u5206\u4E3A\u4E8C" aria-hidden="true">#</a></h4><p>Cheney \u7B97\u6CD5\u662F\u4E00\u79CD\u91C7\u7528\u590D\u5236\u7684\u65B9\u5F0F\u5B9E\u73B0\u7684\u5783\u573E\u56DE\u6536\u7B97\u6CD5\u3002\u5C06\u5806\u5185\u5B58\u4E00\u5206\u4E3A\u4E8C\uFF0C\u6BCF\u4E00\u90E8\u5206\u7A7A\u95F4\u79F0\u4E3A semispace\u3002\u5728\u8FD9\u4E24\u4E2A semispace \u7A7A\u95F4\u4E2D\uFF0C\u53EA\u6709\u4E00\u4E2A\u5904\u4E8E\u4F7F\u7528\u4E2D\uFF0C\u53E6\u4E00\u4E2A\u5904\u4E8E\u95F2\u7F6E\u72B6\u6001\u3002\u5904\u4E8E\u4F7F\u7528\u72B6\u6001\u7684 semispace \u7A7A\u95F4\u79F0\u4E3A From \u7A7A\u95F4\uFF0C\u5904\u4E8E\u95F2\u7F6E\u72B6\u6001\u7684\u7A7A\u95F4\u79F0\u4E3A To \u7A7A\u95F4\u3002</p><h4 id="\u7FFB\u8F6C" tabindex="-1">\u7FFB\u8F6C <a class="header-anchor" href="#\u7FFB\u8F6C" aria-hidden="true">#</a></h4><p>\u5F00\u59CB\u5206\u914D\u5BF9\u8C61\u65F6\uFF0C\u5148\u5728 From \u4E2D\u95F4\u4E2D\u8FDB\u884C\u5206\u914D\u3002\u8FDB\u884C\u5783\u573E\u56DE\u6536\u65F6\uFF0C\u4F1A\u68C0\u67E5 From \u7A7A\u95F4\u4E2D\u7684\u5B58\u6D3B\u5BF9\u8C61\uFF0C\u8FD9\u4E9B\u5B58\u6D3B\u5BF9\u8C61\u5C06\u88AB\u590D\u5236\u5230 To \u7A7A\u95F4\u4E2D\uFF0C\u800C\u975E\u5B58\u6D3B\u5BF9\u8C61\u5360\u7528\u7684\u7A7A\u95F4\u5C06\u4F1A\u88AB\u91CA\u653E\u3002</p><p>\u5B8C\u6210\u590D\u5236\u540E\uFF0CFrom \u7A7A\u95F4\u548C To \u7A7A\u95F4\u7684\u89D2\u8272\u53D1\u751F\u5BF9\u6362\uFF08\u7FFB\u8F6C\uFF09\u3002</p><h4 id="\u7F3A\u70B9" tabindex="-1">\u7F3A\u70B9 <a class="header-anchor" href="#\u7F3A\u70B9" aria-hidden="true">#</a></h4><p>\u8BE5\u7B97\u6CD5\u7684\u7F3A\u70B9\u5F88\u660E\u663E\uFF0C\u53EA\u80FD\u4F7F\u7528\u5806\u5185\u5B58\u4E2D\u7684\u4E00\u534A\uFF0C\u8FD9\u662F\u7531\u5212\u5206\u7A7A\u95F4\u548C\u590D\u5236\u673A\u5236\u6240\u51B3\u5B9A\u7684\u3002\u4F46 Scavenge \u7B97\u6CD5\u7531\u4E8E\u53EA\u590D\u5236\u5B58\u6D3B\u7684\u5BF9\u8C61\uFF0C\u5E76\u4E14\u5BF9\u4E8E\u751F\u547D\u5468\u671F\u77ED\u7684\u573A\u666F\u5B58\u6D3B\u5BF9\u8C61\u53EA\u5360\u5C11\u90E8\u5206\uFF0C\u6240\u4EE5\u5728\u65F6\u95F4\u6548\u7387\u4E0A\u6709\u4F18\u5F02\u7684\u8868\u73B0\u3002</p><p>\u53EF\u4EE5\u53D1\u73B0 Scavenge \u7B97\u6CD5\u975E\u5E38\u9002\u5408\u5E94\u7528\u5728\u65B0\u751F\u4EE3\u4E2D\uFF0C\u56E0\u4E3A\u65B0\u751F\u4EE3\u4E2D\u5BF9\u8C61\u7684\u751F\u547D\u5468\u671F\u8F83\u77ED\u3002</p><h4 id="\u664B\u5347" tabindex="-1">\u664B\u5347 <a class="header-anchor" href="#\u664B\u5347" aria-hidden="true">#</a></h4><p>\u5F53\u4E00\u4E2A\u5BF9\u8C61\u7ECF\u8FC7\u591A\u6B21\u590D\u5236\u4F9D\u7136\u5B58\u6D3B\u65F6\uFF0C\u5B83\u5C06\u4F1A\u88AB\u8BA4\u4E3A\u662F\u751F\u547D\u5468\u671F\u8F83\u957F\u7684\u5BF9\u8C61\u3002\u8FD9\u79CD\u8F83\u957F\u751F\u547D\u5468\u671F\u7684\u5BF9\u8C61\u968F\u540E\u4F1A\u88AB\u79FB\u52A8\u5230\u8001\u751F\u4EE3\u4E2D\uFF0C\u91C7\u7528\u65B0\u7684\u7B97\u6CD5\u8FDB\u884C\u7BA1\u7406\u3002</p><p>\u5BF9\u8C61\u4ECE\u65B0\u751F\u4EE3\u79FB\u52A8\u5230\u8001\u751F\u4EE3\u4E2D\u7684\u8FC7\u7A0B\u79F0\u4E3A\u664B\u5347\u3002</p><p>\u5728\u5355\u7EAF\u7684 Scavenge \u8FC7\u7A0B\u4E2D\uFF0C From \u7A7A\u95F4\u4E2D\u7684\u5B58\u6D3B\u5BF9\u8C61\u4F1A\u88AB\u590D\u5236\u5230 To \u7A7A\u95F4\u4E2D\u53BB\uFF0C\u7136\u540E\u4E24\u4E2A\u7A7A\u95F4\u7684\u89D2\u8272\u8FDB\u884C\u5151\u6362\u3002\u4F46\u5728\u5206\u4EE3\u5F0F\u5783\u573E\u56DE\u6536\u7684\u524D\u63D0\u4E0B\uFF0CFrom \u7A7A\u95F4\u7684\u5B58\u6D3B\u5BF9\u8C61\u5728\u590D\u5236\u5230 To \u7A7A\u95F4\u4E4B\u524D\u9700\u8981\u8FDB\u884C\u68C0\u67E5\u3002\u5982\u679C\u7B26\u5408\u5BF9\u8C61\u664B\u5347\u7684\u6761\u4EF6\uFF0C\u5219\u9700\u8981\u5C06\u5B58\u6D3B\u5468\u671F\u957F\u7684\u5BF9\u8C61\u79FB\u52A8\u5230\u8001\u751F\u4EE3\u4E2D\uFF0C\u5B8C\u6210\u5BF9\u8C61\u664B\u5347</p><h2 id="\u8001\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536-mark-sweep-mark-compact" tabindex="-1">\u8001\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536 (Mark-Sweep &amp; Mark-Compact) <a class="header-anchor" href="#\u8001\u751F\u4EE3\u7684\u5783\u573E\u56DE\u6536-mark-sweep-mark-compact" aria-hidden="true">#</a></h2><h4 id="\u4E0D\u7EE7\u7EED\u4F7F\u7528-scavenge-\u56DE\u6536\u7684\u539F\u56E0" tabindex="-1">\u4E0D\u7EE7\u7EED\u4F7F\u7528 Scavenge \u56DE\u6536\u7684\u539F\u56E0 <a class="header-anchor" href="#\u4E0D\u7EE7\u7EED\u4F7F\u7528-scavenge-\u56DE\u6536\u7684\u539F\u56E0" aria-hidden="true">#</a></h4><ul><li>\u8001\u751F\u4EE3\u4E2D\u7684\u5BF9\u8C61\u5360\u6BD4\u91CD\u5927\uFF0C\u5B58\u6D3B\u5BF9\u8C61\u591A\uFF0C\u590D\u5236\u5B58\u6D3B\u5BF9\u8C61\u7684\u6548\u7387\u5C06\u4F1A\u5F88\u4F4E</li><li>\u6D6A\u8D39\u4E00\u534A\u7A7A\u95F4\u7684\u95EE\u9898</li></ul><h4 id="mark-sweep-\u6807\u8BB0\u6E05\u9664" tabindex="-1">Mark-Sweep (\u6807\u8BB0\u6E05\u9664) <a class="header-anchor" href="#mark-sweep-\u6807\u8BB0\u6E05\u9664" aria-hidden="true">#</a></h4><p>\u5206\u4E3A\u6807\u8BB0\u548C\u6E05\u9664\u3002</p><p><strong>\u6807\u8BB0\uFF1A</strong> \u8BE5\u9636\u6BB5\u4F1A\u904D\u5386\u5806\u4E2D\u7684\u6240\u6709\u5BF9\u8C61\uFF0C\u5E76\u6807\u8BB0\u6D3B\u7740\u7684\u5BF9\u8C61\uFF0C\u5728\u968F\u540E\u7684\u6E05\u9664\u9636\u6BB5\u4E2D\uFF0C\u53EA\u6E05\u9664\u6CA1\u6709\u88AB\u6807\u8BB0\u7684\u5BF9\u8C61\u3002Scavenge \u53EA\u590D\u5236\u6D3B\u7740\u7684\u5BF9\u8C61\uFF0C\u56E0\u4E3A\u6D3B\u5BF9\u8C61\u5728\u65B0\u751F\u4EE3\u4E2D\u53EA\u5360\u8F83\u5C0F\u7684\u90E8\u5206\u3002 Mark-Sweep \u53EA\u6E05\u7406\u6B7B\u4EA1\u5BF9\u8C61\uFF0C\u6B7B\u5BF9\u8C61\u5728\u8001\u751F\u4EE3\u4E2D\u53EA\u5360\u8F83\u5C0F\u7684\u90E8\u5206\u3002</p><p><strong>\u95EE\u9898\uFF1A</strong> \u8FDB\u884C\u4E00\u6B21\u6807\u8BB0\u6E05\u9664\u4E4B\u540E\uFF0C\u5185\u5B58\u7A7A\u95F4\u4F1A\u51FA\u73B0\u4E0D\u8FDE\u7EED\u7684\u72B6\u6001\u3002\uFF08\u4E00\u5757\u4E00\u5757\u7684\uFF09\u5BF9\u540E\u7EED\u7684\u5185\u5B58\u5206\u914D\u4F1A\u9020\u6210\u95EE\u9898\uFF0C\u6BD4\u5982\u65E0\u6CD5\u5BF9\u5927\u5BF9\u8C61\u8FDB\u884C\u5185\u5B58\u5206\u914D\u3002</p><h4 id="mark-compact-\u6807\u8BB0\u6574\u7406" tabindex="-1">Mark-Compact (\u6807\u8BB0\u6574\u7406) <a class="header-anchor" href="#mark-compact-\u6807\u8BB0\u6574\u7406" aria-hidden="true">#</a></h4><p>Mark-Compact \u5BF9\u8C61\u5728\u6807\u8BB0\u6B7B\u4EA1\u540E\uFF0C\u5728\u6574\u7406\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u5C06\u6D3B\u7740\u7684\u5BF9\u8C61\u5F80\u4E00\u7AEF\u79FB\u52A8\uFF0C\u79FB\u52A8\u5B8C\u6210\u540E\uFF0C\u76F4\u63A5\u6E05\u7406\u6389\u8FB9\u754C\u5916\u7684\u5185\u5B58\u3002</p><p>\u5728 V8 \u7684\u56DE\u6536\u7B56\u7565\u4E2D\u4E24\u8005\u662F\u7ED3\u5408\u4F7F\u7528\u7684\u3002</p><h2 id="incremental-marking-\u589E\u91CF\u6807\u8BB0" tabindex="-1">Incremental Marking (\u589E\u91CF\u6807\u8BB0) <a class="header-anchor" href="#incremental-marking-\u589E\u91CF\u6807\u8BB0" aria-hidden="true">#</a></h2><p>\u4EE5\u4E0A\u4ECB\u7ECD\u76843\u79CD\u5783\u573E\u56DE\u6536\u673A\u5236\uFF0C\u90FD\u9700\u8981\u5C06\u5E94\u7528\u903B\u8F91\u6682\u505C\u4E0B\u6765\uFF0C\u5F85\u6267\u884C\u5B8C\u5783\u573E\u56DE\u6536\u540E\u518D\u6062\u590D\u6267\u884C\u9634\u5F71\u903B\u8F91\uFF0C\u8FD9\u79CD\u884C\u4E3A\u88AB\u79F0\u4E3A \u201C\u5168\u505C\u987F\u201D\uFF08stop-the-world\uFF09\u3002</p><p>\u4E00\u6B21\u5C0F\u5783\u573E\u56DE\u6536\u53EA\u6536\u96C6\u65B0\u751F\u4EE3\uFF0C\u65B0\u751F\u4EE3\u4E2D\u5B58\u6D3B\u5BF9\u8C61\u901A\u5E38\u8F83\u5C11\uFF0C\u5373\u4FBF\u5168\u505C\u987F\u5F71\u54CD\u4E5F\u4E0D\u5927\u3002\u4F46\u8001\u751F\u4EE3\u4E2D\u901A\u5E38\u5B58\u6D3B\u5BF9\u8C61\u8F83\u591A\uFF0C\u5168\u5806\u5783\u573E\u56DE\u6536\uFF08full \u5783\u573E\u56DE\u6536\uFF09\u7684\u6807\u8BB0\uFF0C\u6E05\u7406\uFF0C\u6574\u7406\u7B49\u52A8\u4F5C\u9020\u6210\u7684\u505C\u987F\u5C31\u4F1A\u5F88\u53EF\u6015\u3002</p><p>\u4E3A\u4E86\u964D\u4F4E\u5168\u5806\u5783\u573E\u56DE\u6536\u5E26\u6765\u7684\u505C\u987F\u4E8B\u4EF6\u3002V8 \u5728\u6807\u8BB0\u9636\u6BB5\u65F6\u4F1A\u5C06\u539F\u672C\u9700\u8981\u4E00\u53E3\u6C14\u505C\u987F\u5B8C\u6210\u7684\u52A8\u4F5C\u6539\u4E3A\u589E\u91CF\u6807\u8BB0\uFF08Incremental Marking\uFF09\uFF0C\u5373\u62C6\u5206\u4E3A\u8BB8\u591A\u5C0F\u201C\u6B65\u8FDB\u201D\uFF0C\u6BCF\u505A\u5B8C\u4E00\u201C\u6B65\u8FDB\u201D\u5C31\u8BA9 JavaScript \u5E94\u7528\u903B\u8F91\u6267\u884C\u4E00\u4F1A\uFF0C<strong>\u5783\u573E\u56DE\u6536\u4E0E\u5E94\u7528\u903B\u8F91\u4EA4\u66FF\u6267\u884C\u76F4\u5230\u6807\u8BB0\u9636\u6BB5\u5B8C\u6210</strong>\u3002</p><p>V8 \u540E\u7EED\u8FD8\u5F15\u5165\u4E86\u5EF6\u8FDF\u6E05\u7406\uFF08lazy sweeping\uFF09\u4E0E\u589E\u91CF\u5F0F\u6574\u7406\uFF08Incremental compaction\uFF09\uFF0C\u8BA9\u6E05\u7406\u548C\u6574\u7406\u52A8\u4F5C\u4E5F\u53D8\u6210\u589E\u91CF\u5F0F\u7684\u3002</p><h2 id="\u5185\u5B58\u6CC4\u6F0F" tabindex="-1">\u5185\u5B58\u6CC4\u6F0F <a class="header-anchor" href="#\u5185\u5B58\u6CC4\u6F0F" aria-hidden="true">#</a></h2><p>\u5185\u5B58\u6CC4\u6F0F\u662F\u6307\u6211\u4EEC\u5DF2\u7ECF\u65E0\u6CD5\u518D\u901A\u8FC7 js \u4EE3\u7801\u6765\u5F15\u7528\u5230\u67D0\u4E2A\u5BF9\u8C61\uFF0C\u4F46\u5783\u573E\u56DE\u6536\u5668\u5374\u8BA4\u4E3A\u8FD9\u4E2A\u5BF9\u8C61\u8FD8\u5728\u88AB\u5F15\u7528\uFF0C\u56E0\u6B64\u5728\u56DE\u6536\u7684\u65F6\u5019\u4E0D\u4F1A\u91CA\u653E\u5B83\u3002\u5BFC\u81F4\u4E86\u5206\u914D\u7684\u8FD9\u5757\u5185\u5B58\u6C38\u8FDC\u4E5F\u65E0\u6CD5\u88AB\u91CA\u653E\u51FA\u6765\u3002\u5982\u679C\u8FD9\u6837\u7684\u60C5\u51B5\u8D8A\u6765\u8D8A\u591A\uFF0C\u4F1A\u5BFC\u81F4\u5185\u5B58\u4E0D\u591F\u7528\u800C\u7CFB\u7EDF\u5D29\u6E83\u3002</p><p>\u9020\u6210\u5185\u5B58\u6CC4\u9732\u7684\u60C5\u51B5\u6709\u5F88\u591A\uFF0C\u4F46\u672C\u8D28\u53EA\u6709\u4E00\u4E2A\uFF0C\u5E94\u8BE5\u56DE\u6536\u7684\u5BF9\u8C61\u51FA\u73B0\u610F\u5916\u800C\u6CA1\u6709\u88AB\u56DE\u6536\uFF0C\u53D8\u6210\u4E86\u5E38\u9A7B\u5728\u8001\u751F\u4EE3\u7684\u5BF9\u8C61\u3002</p><p>\u901A\u8FC7\u9020\u6210\u5185\u5B58\u6CC4\u9732\u7684\u539F\u56E0\uFF1A</p><ul><li>\u7F13\u5B58</li><li>\u961F\u5217\u6D88\u8D39\u4E0D\u53CA\u65F6</li><li>\u4F5C\u7528\u57DF\u672A\u91CA\u653E</li></ul>',37),c=[t];function h(l,p,s,d,o,m){return r(),a("div",null,c)}const u=e(i,[["render",h]]);export{k as __pageData,u as default};
