import{_ as e,c as a,o as r,a as s}from"./app.4a35dd4a.js";const _=JSON.parse('{"title":"XSS\u548CCSRF","description":"","frontmatter":{"title":"XSS\u548CCSRF","outline":[2,6]},"headers":[{"level":2,"title":"XSS","slug":"xss","link":"#xss","children":[]},{"level":2,"title":"CSRF","slug":"csrf","link":"#csrf","children":[]}],"relativePath":"summary/Security/XSS-CSRF.md","lastUpdated":1677469957000}'),i={name:"summary/Security/XSS-CSRF.md"},t=s('<p>XSS\u548CCSRF</p><h2 id="xss" tabindex="-1">XSS <a class="header-anchor" href="#xss" aria-hidden="true">#</a></h2><h4 id="\u4EC0\u4E48\u662Fxss" tabindex="-1">\u4EC0\u4E48\u662FXSS <a class="header-anchor" href="#\u4EC0\u4E48\u662Fxss" aria-hidden="true">#</a></h4><p>Cross-Site Scripting(\u8DE8\u7AD9\u811A\u672C\u653B\u51FB)\uFF0C\u7B80\u79F0XSS(\u907F\u514D\u548CCSS\u91CD\u540D)\uFF0C\u662F\u4E00\u79CD\u4EE3\u7801\u6CE8\u5165\u653B\u51FB\u3002\u653B\u51FB\u8005\u901A\u8FC7\u5728\u76EE\u6807\u7F51\u7AD9\u4E0A\u6CE8\u5165\u6076\u610F\u811A\u672C\uFF0C\u4F7F\u4E4B\u5728\u7528\u6237\u7684\u6D4F\u89C8\u5668\u4E0A\u8FD0\u884C\u3002\u5229\u7528\u8FD9\u4E9B\u6076\u610F\u811A\u672C\uFF0C\u653B\u51FB\u8005\u53EF\u83B7\u53D6\u7528\u6237\u7684\u654F\u611F\u4FE1\u606F\u5982Cookie\u3001SessionID\u7B49\uFF0C\u8FDB\u800C\u5371\u5BB3\u6570\u636E\u5B89\u5168</p><h4 id="xss\u5206\u7C7B" tabindex="-1">XSS\u5206\u7C7B <a class="header-anchor" href="#xss\u5206\u7C7B" aria-hidden="true">#</a></h4><ul><li><strong>\u53CD\u5C04\u578B(Reflected XSS)</strong> \u6240\u8C13\u53CD\u5C04\u578B\u5C31\u662F\uFF0C\u7528\u6237\u5728\u7F51\u9875\u4E2D\u8F93\u5165\u7684\u5185\u5BB9\u7ECF\u9875\u9762\u63D0\u4EA4\u5230\u670D\u52A1\u5668\u540E\uFF0C\u670D\u52A1\u5668\u6CA1\u6709\u5BF9\u8BE5\u6570\u636E\u8FDB\u884C\u5B58\u50A8\u5230\u6570\u636E\u5E93\uFF0C\u800C\u662F\u539F\u5C01\u4E0D\u52A8\u7684\u53CD\u5C04\u56DE\u9875\u9762\u4E2D\uFF0C\u5373\u7528\u6237\u8F93\u5165\u4EC0\u4E48\u5185\u5BB9\uFF0C\u670D\u52A1\u5668\u5C31\u5C06\u5176\u539F\u5C01\u4E0D\u52A8\u7684\u663E\u793A\u5230\u9875\u9762\u4E2D\uFF0C\u4ECE\u800C\u8BA9\u7528\u6237\u7ACB\u5373\u88AB\u653B\u51FB\u3002\u6700\u5E38\u89C1\u7684\u5C31\u662F\u641C\u7D22\u5F15\u64CE\uFF0C\u5F53\u6211\u4EEC\u641C\u7D22\u4E00\u4E2A\u65E0\u6CD5\u641C\u7D22\u5230\u7684\u5185\u5BB9\u65F6\uFF0C\u641C\u7D22\u5F15\u64CE\u901A\u5E38\u4F1A\u76F4\u63A5\u5728\u9875\u9762\u4E2D\u63D0\u793A\uFF0C\u8BE5\u5185\u5BB9\u65E0\u6CD5\u641C\u7D22\u5230\uFF0C\u6B64\u65F6\u5982\u679C\u7528\u6237\u641C\u7D22\u7684\u5185\u5BB9\u4E2D\u5305\u542B\u4E00\u4E9B\u653B\u51FB\u6027\u7684\u811A\u672C\u540C\u65F6\u670D\u52A1\u5668\u53C8\u6CA1\u6709\u5BF9\u8FD9\u4E9B\u5185\u5BB9\u8FDB\u884C\u5904\u7406\uFF0C\u90A3\u4E48\u5C31\u4F1A\u88AB\u653B\u51FB</li><li><strong>\u5B58\u50A8\u578B (Stored XSS)</strong> \u6240\u8C13\u5B58\u50A8\u578B\u5C31\u662F\uFF0C\u7528\u6237\u5728\u7F51\u9875\u4E2D\u8F93\u5165\u7684\u5185\u5BB9\u7ECF\u9875\u9762\u63D0\u4EA4\u5230\u670D\u52A1\u5668\u540E\uFF0C\u670D\u52A1\u5668\u5C06\u8BE5\u6570\u636E\u5148\u539F\u5C01\u4E0D\u52A8\u7684\u5B58\u50A8\u5230\u6570\u636E\u5E93\u4E2D\uFF0C\u7B49\u5230\u5176\u4ED6\u7528\u6237\u8BBF\u95EE\u8BE5\u7F51\u7AD9\u7684\u65F6\u5019\uFF0C\u4ECE\u670D\u52A1\u5668\u4E2D\u8BFB\u53D6\u51FA\u6570\u636E\uFF0C\u4ECE\u800C\u8BA9\u5176\u4E2D\u5305\u542B\u7684\u811A\u672C\u6267\u884C\uFF0C\u6240\u4EE5\u5B58\u50A8\u578B\u4E0D\u662F\u7ACB\u5373\u88AB\u653B\u51FB\uFF0C\u800C\u662F\u7B49\u5176\u4ED6\u7528\u6237\u8BBF\u95EE\u8BE5\u7F51\u7AD9\u7684\u65F6\u5019\u624D\u751F\u6548\uFF0C\u4F46\u662F\u5176\u7531\u4E8E\u88AB\u5B58\u50A8\u5230\u4E86\u6570\u636E\u5E93\u4E2D\uFF0C\u6240\u4EE5\u5176\u653B\u51FB\u8303\u56F4\u66F4\u5E7F\u4E3A\u7F51\u7AD9\u7684\u6240\u6709\u8BBF\u95EE\u8005\u3002\u6700\u5E38\u89C1\u7684\u5C31\u662F\u67D0\u4E2A\u9ED1\u5BA2\u7528\u6237\u63D0\u4EA4\u4E86\u4E00\u7BC7\u6587\u7AE0\uFF0C\u800C\u6587\u7AE0\u4E2D\u88AB\u6CE8\u5165\u4E86\u4E00\u6BB5\u811A\u672C\uFF0C\u800C\u8BE5\u6587\u7AE0\u4F1A\u88AB\u63D0\u4EA4\u5230\u6570\u636E\u5E93\u4E2D\uFF0C\u6240\u4EE5\u7B49\u5230\u5176\u4ED6\u7528\u6237\u8BBF\u95EE\u8BE5\u6587\u7AE0\u7684\u65F6\u5019\u5C31\u4F1A\u88AB\u653B\u51FB\u3002 \u53CD\u5C04\u578B\u548C\u5B58\u50A8\u578BXSS\u7684\u533A\u522B\u662F\u770B\u7528\u6237\u63D0\u4EA4\u7684\u6570\u636E\u6709\u6CA1\u6709\u88AB\u670D\u52A1\u5668\u5B58\u50A8\u8D77\u6765\u3002</li><li><strong>\u57FA\u4E8EDOM (DOM-based or local XSS)</strong> \u6240\u8C13\u57FA\u4E8EDOM\u7C7B\u578B\u5C31\u662F\uFF0C\u4F7F\u7528\u5230\u7684\u6570\u636E\u6CA1\u6709\u7ECF\u8FC7\u670D\u52A1\u5668\uFF0C\u800C\u662F\u76F4\u63A5\u4ECEDOM\u3001Window\u7B49\u5BF9\u8C61\u4E2D\u83B7\u53D6\uFF0C\u6BD4\u5982\uFF0Cdocument.location\u3001document.URL\u3001document.referrer\uFF0C\u5C06\u8FD9\u4E9B\u6570\u636E\u63D2\u5165\u5230DOM\u4E2D\u540E\u5BFC\u81F4\u7684XSS\u653B\u51FB\u3002\u6BD4\u8F83\u5E38\u89C1\u7684\u5C31\u662F\uFF0C\u5F53\u6211\u4EEC\u5728\u67D0\u4E2A\u7F51\u7AD9\u6CE8\u518C\u6210\u529F\u540E\uFF0C\u901A\u5E38\u4F1A\u8DF3\u8F6C\u5230\u4E00\u4E2A\u9A8C\u8BC1\u9875\u9762\uFF0C\u9A8C\u8BC1\u9875\u9762\u5185\u5BB9\u4E3B\u8981\u5305\u62EC\u591A\u5C11\u79D2\u4E4B\u540E\u81EA\u52A8\u8DF3\u8F6C\u5230\u67D0\u4E2A\u9875\u9762\uFF0C\u6216\u8005\u70B9\u51FB\u94FE\u63A5\u7ACB\u5373\u8DF3\u8F6C\u5230\u67D0\u4E2A\u9875\u9762\uFF0C\u5982: <code>http://localhost:3000/validate.html?redirectTo=javascript:alert(&#39;xss&#39;)</code> \uFF0C\u800C\u6211\u4EEC\u7684\u9875\u9762\u4F1A\u6839\u636E\u4F20\u5165\u7684redirectTo\u7684\u503C\u8FDB\u884C\u8DF3\u8F6C\u3002\u57FA\u4E8EDOM\u7C7B\u578B\u7684XSS\u653B\u51FB\u5173\u952E\u662F\u6570\u636E\u6CA1\u6709\u7ECF\u8FC7\u670D\u52A1\u5668\uFF0C\u800C\u662F\u6765\u81EA\u4E8EDOM\u64CD\u4F5C\u3002</li></ul><h4 id="xss\u9632\u5FA1" tabindex="-1">XSS\u9632\u5FA1 <a class="header-anchor" href="#xss\u9632\u5FA1" aria-hidden="true">#</a></h4><ul><li>\u5C06\u7528\u4E8E\u767B\u5F55\u76F8\u5173\u7684cookie\u8BBE\u7F6E\u4E3AhttpOnly\uFF0C\u907F\u514D\u88AB\u5BA2\u6237\u7AEF\u811A\u672C\u76F4\u63A5\u8BFB\u53D6\u3002</li><li>\u5BF9\u7528\u6237\u8F93\u5165\u548C\u8F93\u51FA\u8FDB\u884C\u8FC7\u6EE4\uFF0C\u5BF9\u5176\u4E2D\u6CE8\u5165\u7684\u811A\u672C\u8FDB\u884C\u8F6C\u4E49</li></ul><h2 id="csrf" tabindex="-1">CSRF <a class="header-anchor" href="#csrf" aria-hidden="true">#</a></h2><h4 id="\u4EC0\u4E48\u662Fcsrf" tabindex="-1">\u4EC0\u4E48\u662FCSRF <a class="header-anchor" href="#\u4EC0\u4E48\u662Fcsrf" aria-hidden="true">#</a></h4><p>CSRF \u5168\u79F0\u4E3A(Cross-Site Request Forgery)\uFF0C\u5373\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\uFF0C\u4E5F\u79F0\u4E3AOne Click Attack\uFF0C\u4E00\u952E\u653B\u51FB\uFF0C\u56E0\u4E3A\u5176\u901A\u5E38\u4F1A\u901A\u8FC7\u4E00\u4E2A\u56FE\u7247\u6216\u8005\u94FE\u63A5\u5E76\u8BF1\u5BFC\u7528\u6237\u53BB\u70B9\u51FB\uFF0C\u5F53\u7528\u6237\u70B9\u51FB\u8FD9\u4E2A\u56FE\u7247\u6216\u8005\u94FE\u63A5\u540E\uFF0C\u8FD9\u4E2A\u94FE\u63A5\u901A\u5E38\u5305\u542B\u4E86\u4E00\u4E9B\u653B\u51FB\u6027\u7684\u64CD\u4F5C\u548C\u53C2\u6570\uFF0C\u5373\u6240\u8C13\u7684\u4F2A\u9020\u7684\u8BF7\u6C42\uFF0C\u5C31\u4F1A\u4EE5\u7528\u6237\u7684\u8EAB\u4EFD\u5411\u670D\u52A1\u5668\u53D1\u8D77\u8FD9\u4E2A\u4F2A\u9020\u7684\u8BF7\u6C42\uFF0C\u670D\u52A1\u5668\u6536\u5230\u8FD9\u4E2A\u4F2A\u9020\u7684\u8BF7\u6C42\u540E\uFF0C\u5C31\u4F1A\u8BA4\u4E3A\u662F\u7528\u6237\u81EA\u5DF1\u8BB8\u53EF\u7684\u64CD\u4F5C\uFF0C\u5BFC\u81F4\u7528\u6237\u6570\u636E\u4E22\u5931\u7B49\u3002</p><h4 id="csrf\u7684\u6761\u4EF6" tabindex="-1">CSRF\u7684\u6761\u4EF6 <a class="header-anchor" href="#csrf\u7684\u6761\u4EF6" aria-hidden="true">#</a></h4><p>\u7531\u4E8ECSRF\u662F\u4F2A\u9020\u7528\u6237\u8BF7\u6C42\uFF0C\u5E76\u4E14\u4EE5\u7528\u6237\u7684\u8EAB\u4EFD\u5411\u670D\u52A1\u5668\u53D1\u8D77\u8BF7\u6C42\uFF0C\u5BFC\u81F4\u7528\u6237\u6570\u636E\u4E22\u5931\uFF0C\u6240\u4EE5\u7528\u6237\u5FC5\u987B\u5148\u767B\u5F55\u83B7\u53D6\u5BF9\u5E94\u7684\u767B\u5F55cookie\u4E4B\u540E\uFF0C\u4F2A\u9020\u7684\u8BF7\u6C42\u624D\u4F1A\u751F\u6548\uFF0C\u624D\u4F1A\u5BFC\u81F4\u7528\u6237\u6570\u636E\u4E22\u5931\u3002\u6240\u4EE5CSRF\u653B\u51FB\u7684\u524D\u63D0\u6761\u4EF6\u662F:</p><ul><li>\u7528\u6237\u5FC5\u987B\u5148\u767B\u5F55\u4FE1\u4EFB\u7F51\u7AD9\uFF0C\u5E76\u4E14\u5728\u672C\u5730\u751F\u6210\u5BF9\u5E94\u7684cookie\uFF1B</li><li>\u5728\u7528\u6237\u6CA1\u6709\u767B\u51FA\u7684\u60C5\u51B5\u4E0B\uFF0C\u8BBF\u95EE\u4E86\u5371\u9669\u7F51\u7AD9\uFF1B</li></ul><h4 id="csrf\u9632\u5FA1" tabindex="-1">CSRF\u9632\u5FA1 <a class="header-anchor" href="#csrf\u9632\u5FA1" aria-hidden="true">#</a></h4><ul><li>\u589E\u52A0token\u6821\u9A8C</li><li>\u6821\u9A8C\u8BF7\u6C42\u5934\u7684referer\u5C5E\u6027\u503C</li></ul>',16),d=[t];function l(c,o,S,n,h,u){return r(),a("div",null,d)}const p=e(i,[["render",l]]);export{_ as __pageData,p as default};
